const jsYaml = require('js-yaml');

const jsonObject = {
  "employees": [
    {
      "name": "John Doe",
      "age": 30,
      "hobbies": [
        "reading",
        "running"
      ],
      "is_married": false
    },
    {
      "name": "Jane Smith",
      "age": 25,
      "hobbies": [
        "painting",
        "hiking"
      ],
      "is_married": true
    }
  ]
};

function convertJsonToYaml(jsonObj) {
  try {
    const yamlString = jsYaml.dump(jsonObj);
    return yamlString;
  } catch (error) {
