```cpp
#include <iostream>
#include <string>

enum class State {
    START,
    FOUND_T,
    FOUND_TM,
    ERROR
};

bool is_valid_word(const std::string& word) {
    State state = State::START;
    int index = 0;
    for (char c : word) {
        switch (state) {
            case State::START:
                if (c == 't') {
                    state = State::FOUND_T;
                } else {
                    state = State::ERROR;
                }
                break;
            case State::FOUND_T:
                if (c == 'm') {
                    state = State::FOUND_TM;
                } else {
                    state = State::ERROR;
                }
                break;
            case State::FOUND_TM:
                if (c == 'm' && index == (word.length() - 1)) {
                    return true;
                } else {
                    state = State::ERROR;
                }
                break;
            case State::ERROR:
                return false;
        }
        index++;
    }
    return state == State::FOUND_TM;
}

int main() {
    std::string input;
    std::cout << "Enter a string to check if it's a valid word: ";
    std::cin >> input;

    if (is_valid_word(input)) {
        std::cout << "The word '" << input << "' is valid in the given language.\n";
    } else {
        std::cout << "The word '" << input << "' is NOT valid in the given language.\n";
    }

    return 0;
}
```

