function calcDegreeDifficulty(equations) {
  let degreeDifficulty = [];
  let variablesCount = 0;
  for (let i = 0; i < equations.length; i++) {
    let numVariables = 0;
    // find the number of variables in the math equation
    for (let j = 0; j < equations[i].split('=').length; j++) {
      if (equations[i][j] == 'x') numVariables += 1;
    }
    // find the degree of difficulty for the math equation
    let numOperators = equations.length + 1;
    let numTerms = equations.length + 1;
    let maxPower = Math.max(equations[i].split('x').length);
    let numerator = 1 + numOperators * numTerms * maxPower;
    let denominator = 1 + numOperators * numTerms + maxPower;
    let difficulty = Math.pow(numerator, 1 / denominator);
    degreeDifficulty.push({difficulty: difficulty, numVariables: numVariables});
    variablesCount += numVariables;
  }
  return { degreeDifficulty, variablesCount };
}
