```c++
#include <sqlite3.h>
#include <string>
#include <vector>

using namespace std;

class CRegistry {
public:
    CRegistry(const string& path);
    ~CRegistry();

    bool Write(const string& key, const void* buffer, size_t size);
    bool Read(const string& key, void* buffer, size_t size);
    bool Delete(const string& key);

private:
    sqlite3* db;
    string path;
};

CRegistry::CRegistry(const string& path) : path(path) {
    sqlite3* db = nullptr;
    if (sqlite3_open(path.c_str(), &db) != SQLITE_OK) {
        cout << "Error opening database: " << sqlite3_errmsg(db) << endl;
        exit(1);
    }
    this->db = db;
}

CRegistry::~CRegistry() {
    if (this->db) {
        int rc = sqlite3_close(this->db);
        if (rc != SQLITE_OK) {
            cout << "Error closing database: " << sqlite3_errmsg(this->db) << endl;
        }
    }
}

bool CRegistry::Write(const string& key, const void* buffer, size_t size) {
    int rc = sqlite3_exec(this->db, "INSERT INTO values (key, value) VALUES (?, ?)",
                                      reinterpret_cast<sqlite3_value**>(&key),
                                      reinterpret_cast<sqlite3_value**>(&buffer),
                                      NULL);
    if (rc != SQLITE_OK) {
        cout << "Error writing to database: " << sqlite3_errmsg(this->db) << endl;
        return false;
    }
    return true;
}

bool CRegistry::Read(const string& key, void* buffer, size_t size) {
    int rc = sqlite3_exec(this->db, "SELECT value FROM values WHERE key = ?",
                                      reinterpret_cast<sqlite3_value**>(&key),
                                      reinterpret_cast<sqlite3_value**>(&buffer),
                                      NULL);
    if (rc != SQLITE_OK) {
        cout << "Error reading from database: " << sqlite3_errmsg(this->db) << endl;
        return false;
    }
    return true;
}

bool CRegistry::Delete(const string& key) {
    int rc = sqlite3_exec(this->db, "DELETE FROM values WHERE key = ?",
                                      reinterpret_cast<sqlite3_value**>(&key),
                                      NULL);
    if (rc != SQLITE_OK) {
        cout << "Error deleting from database: " << sqlite3_errmsg(this->db) << endl;
        return false;
    }
    return true;
}
```

